import React, { useState, useEffect } from 'react';
import './App.css';
import './Card.css';

function App() {
  const [isLoggedIn, LoggedIn] = useState(false);
  const [seconds, setSeconds] = useState(0);

  const handleLogin = (e) => {

    LoggedIn(true);
  };

  const handleLogout = () => {
   LoggedIn(false);
    setSeconds(0); 
  };

  useEffect(() => {
    let interval;
    if (isLoggedIn) {
      interval = setInterval(() => {
        setSeconds((prev) => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isLoggedIn]);

  return (
    <div>
      {!isLoggedIn ? (

        <div className=''>
          <h3 className="heading">Login Form</h3>
          <form onSubmit={handleLogin}>
            <input type="text" id="username" name="username" className="input" placeholder='User Name' />
            <br />
            <input type="password" id="password" name="password" className="input" placeholder='Password' />
            <br />
            <button type="submit" className="button1">Login</button>
          </form>
        </div>
      ) : (

        <div className='card'>
          <h3 className="heading">Welcome</h3>
          <p className="paragraph">You have been logged in for {seconds} seconds</p>
          <button className="button2" onClick={handleLogout}>Logout</button>
        </div>
      )}
    </div>
  );
}

export default App;
